mapper.select:
select  hotel.id,hotel.hotelname,hotel.address,hotel.businessdistrict,hotel.Introduction,hotel.facilities,hotel.Star,hotel.score,avg(room.price) as averagePrice
from hotel
left join
room on hotel.id=room.hotel_id GROUP BY hotel.id


        and hotel.id =#{hotelId}


        and address=#{address} and businessdistrict=#{businessdistrict}

select h.*,r.roomtype,r.rooms,r.price,SUM(od.rooms) as roomNumber,IF((r.rooms-SUM(od.rooms))>0,(r.rooms-SUM(od.rooms)),0)as numbers,IF((r.rooms-SUM(od.rooms))>0,'有空房','无空房') as state
from `room` r,hotel h,
(select od.hotelname,od.roomtype,od.hotel_id,od.`status`,od.room_id,od.rooms,od.startdate,od.enddate
from  `order` od
where od.`status`='未执行') od


  od.room_id=r.id and h.id=od.hotel_id

      and h.address=#{hotelVo.address}


      and r.roomtype=#{hotelVo.roomType}


      and h.businessdistrict=#{hotelVo.businessDistrict}


      and h.hotelname=#{hotelVo.hotelName}


      and h.Star=#{hotelVo.star}


      and h.score BETWEEN #{hotelVo.score[0]} and #{hotelVo.score[1]}


      and r.price BETWEEN #{hotelVo.price[0]} and #{hotelVo.price[1]}


      AND od.startdate >= #{hotelVo.startTime}


      AND od.enddate <= #{hotelVo.endTime}


GROUP BY r.hotel_id,od.hotelname,od.roomtype,od.rooms,h.id,r.rooms,r.price,r.roomtype,od.room_id

select h.*,r.*,r.rooms as numbers,SUM(0) as roomNumber, IF(r.rooms>0,'有空房','无空房') as state
from `hotel`h,(select r.hotel_id,r.roomtype,r.rooms,r.price
from `room`r where not exists (select * from `order` where `order`.hotel_id=r.hotel_id and `order`.roomtype=r.roomtype))r


    r.hotel_id=h.id

        and h.address=#{hotelVo.address}


        and r.roomtype=#{hotelVo.roomType}


        and h.businessdistrict=#{hotelVo.businessDistrict}


        and h.hotelname=#{hotelVo.hotelName}


        and h.Star=#{hotelVo.star}


        and h.score BETWEEN #{hotelVo.score[0]} and #{hotelVo.score[1]}


        and r.price BETWEEN #{hotelVo.price[0]} and #{hotelVo.price[1]}




GROUP BY h.id,r.roomtype,r.rooms,r.price

SELECT h.id,h.hotelName,h.address,h.businessDistrict,h.introduction,h.facilities,h.star FROM `hotel` h
LEFT JOIN `user` u on h.id=u.hotel_id

    u.hotel_id=#{userId}

mapper.update:
update hotel
address=#{hotel.address}, businessDistrict=#{hotel.businessDistrict}, introduction=#{hotel.introduction}, facilities=#{hotel.facilities}, star=#{hotel.star}
id =#{hotelId}
